import Component from '../Component';

export type Child = Component | string | HTMLElement;
export class ComponentCollection extends Array<Child> {
    public render() {
        return this.map(i => (
            typeof i == 'string' ? i : 
            (i as any).prototype.constructor.name == 'HTMLElement' ? (i as HTMLElement).innerHTML :
            (i as Component).componentEmit('_preRender', (i as Component))[0]
        ))
    }
}

export default ComponentCollection;