import Component from '../';

interface RenderEventHandler {
    preRender: [Component];
    render: [Component];
    rendered: [Component];
}
interface RenderEventReturns {
    preRender: Component;
    render: string;
    rendered: Component;
}
export type RenderEventTypes = keyof RenderEventHandler;

interface PropsEventHandler {
    propUpdate: [{}, {}];
}
interface PropsEventReturns {
    propUpdate: void
}
type PropsEventTypes = keyof PropsEventHandler;

export type ComponentEventTypes = RenderEventTypes | PropsEventTypes;
export interface ComponentEventHandlers extends RenderEventHandler, PropsEventHandler {}
export interface ComponentEventReturns extends RenderEventReturns, PropsEventReturns {}

export interface DOMEventHandler extends DocumentEventMap {};
export type DOMEventTypes = keyof DOMEventHandler; 