import ComponentEventEmitter from './Events/ComponentEventEmitter';
import { Child, ComponentCollection } from '../Collection/ComponentCollection'
import IComponent from './IComponent';

export default class Component extends ComponentEventEmitter implements IComponent {
    constructor(id: string, props: {}, children?: Array<Child>, options?: IComponent) {
        super();

        this.id = id;
        this.props = props;
        this.children = new ComponentCollection(...children, ...options.children);

        for (const prop of Object.keys(props)) {
            this[prop] = props[prop];
        }


    }
}

